css:
.pos-closing-report {
    font-family: 'Courier New', monospace;
    font-size: 12px;
    width: 100%;
    max-width: 280px;
    margin: 0 auto;
    padding: 5px;
    color: #000;
    background: #fff;
}
.pos-closing-report * {
    box-sizing: border-box;
}
.pcr-header {
    text-align: center;
    border-bottom: 1px dashed #000;
    padding-bottom: 8px;
    margin-bottom: 8px;
}
.pcr-company {
    font-size: 14px;
    font-weight: bold;
    text-transform: uppercase;
    margin-bottom: 4px;
}
.pcr-title {
    font-size: 16px;
    font-weight: bold;
    letter-spacing: 2px;
    margin: 8px 0;
}
.pcr-meta {
    font-size: 11px;
    line-height: 1.4;
}
.pcr-section {
    margin: 10px 0;
    padding-bottom: 8px;
    border-bottom: 1px dashed #000;
}
.pcr-section:last-child {
    border-bottom: none;
}
.pcr-section-title {
    font-weight: bold;
    text-transform: uppercase;
    font-size: 11px;
    margin-bottom: 6px;
    text-align: center;
    letter-spacing: 1px;
}
.pcr-summary-row {
    display: flex;
    justify-content: space-between;
    margin: 3px 0;
    font-size: 11px;
}
.pcr-summary-row.total {
    font-weight: bold;
    font-size: 13px;
    margin-top: 6px;
    padding-top: 4px;
    border-top: 1px solid #000;
}
.pcr-payment-table {
    width: 100%;
    font-size: 10px;
    border-collapse: collapse;
}
.pcr-payment-table th {
    text-align: left;
    font-weight: bold;
    padding: 2px 1px;
    border-bottom: 1px solid #000;
    font-size: 9px;
}
.pcr-payment-table td {
    padding: 3px 1px;
    vertical-align: top;
}
.pcr-payment-table .amount {
    text-align: right;
    font-family: 'Courier New', monospace;
}
.pcr-payment-table .diff-positive {
    color: #008000;
}
.pcr-payment-table .diff-negative {
    color: #cc0000;
    font-weight: bold;
}
.pcr-transaction {
    display: flex;
    justify-content: space-between;
    font-size: 10px;
    padding: 2px 0;
    border-bottom: 1px dotted #ccc;
}
.pcr-transaction:last-child {
    border-bottom: none;
}
.pcr-transaction .invoice {
    font-weight: bold;
}
.pcr-transaction .return-badge {
    background: #000;
    color: #fff;
    padding: 0 3px;
    font-size: 8px;
    margin-left: 4px;
}
.pcr-footer {
    margin-top: 15px;
    text-align: center;
}
.pcr-signature {
    margin-top: 30px;
    padding-top: 5px;
    border-top: 1px solid #000;
    font-size: 10px;
}
.pcr-printed-at {
    font-size: 9px;
    color: #666;
    margin-top: 10px;
}
@media print {
    .pos-closing-report {
        max-width: none;
        width: 72mm;
    }
}
